import Test.Hspec

main :: IO()
main = do hspec specs

specs = describe "problem 8" [
    it "gerenate list 5 in 5 elements"
        (five "123456" == ["12345", "23456"]),
    it "ignore numbers with 0"
        (five "1234506" == ["12345"]),
    it "product of numbers"
        (productOfNumbers ["12345", "23456"] == 720),
    it "a string with 50 chars, divide 5 by 5 must return 46 entries"
      (length (five "73167176531331624919225119674426574742355349194934") == 46),
    it "productOf 12345 should be 120"
        (productOf "12345" == 120),
    it "productOf 23456 should be 720"
        (productOf "23456" == 720)
	]

bigNumber = ""

productOf number = product [read (x:[]) :: Int | x <- number]
productOfNumbers list = maximum [ productOf x | x <- list]
five numbers = [(drop (x-1) (take (x+4) numbers)) | x <- [1..(length numbers)-4], not ('0' `elem`  (drop (x-1) (take (x+4) numbers)))]

